import React, { useState } from 'react';

const FontGroupForm = ({ fonts, handleGroupSubmit }) => {
    const [groupName, setGroupName] = useState('');
    const [selectedFonts, setSelectedFonts] = useState([]);

    const handleFontChange = (e) => {
        const fontId = e.target.value;
        if (selectedFonts.includes(fontId)) {
            setSelectedFonts(selectedFonts.filter(id => id !== fontId));
        } else {
            setSelectedFonts([...selectedFonts, fontId]);
        }
    };

    const onSubmit = () => {
        if (groupName && selectedFonts.length > 0) {
            handleGroupSubmit(groupName, selectedFonts);
        } else {
            alert("Please provide a group name and select at least one font.");
        }
    };

    return (
        <div className="mb-3">
            <input 
                type="text" 
                placeholder="Group Name" 
                value={groupName}
                onChange={(e) => setGroupName(e.target.value)} 
                className="form-control mb-2"
            />
            <h4>Select Fonts for Group</h4>
            {fonts.map((font, index) => (
                <div key={index}>
                    <input 
                        type="checkbox" 
                        value={font.id} 
                        onChange={handleFontChange} 
                    />
                    <label className="ms-2">{font.font_name}</label>
                </div>
            ))}
            <button onClick={onSubmit} className="btn btn-success mt-3">Create Group</button>
        </div>
    );
};

export default FontGroupForm;
