import React, { useState, useEffect } from 'react';
import FontUpload from './FontUpload';
import FontGroupForm from './FontGroupForm';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
    const [fonts, setFonts] = useState([]);
    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetchFonts();
        fetchGroups();
    }, []);

    const fetchFonts = async () => {
        const response = await fetch('http://localhost/font_system/backend/index.php?action=getFonts');
        const data = await response.json();
        setFonts(data);
    };

    const fetchGroups = async () => {
        const response = await fetch('http://localhost/font_system/backend/index.php?action=getGroups');
        const data = await response.json();
        setGroups(data);
    };

    const handleUpload = async (file) => {
        const formData = new FormData();
        formData.append('font', file);

        const response = await fetch('http://localhost/font_system/backend/index.php', {
            method: 'POST',
            body: formData,
        });

        const data = await response.json();
        if (data.status === 'success') {
            fetchFonts();
        }
    };

    const handleGroupSubmit = async (groupName, fontGroupData) => {
        if (fontGroupData.length < 2) {
            alert("Please select at least two fonts.");
            return;
        }

        try {
            const response = await fetch('http://localhost/font_system/backend/index.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ groupName, fontGroupData }),
            });

            const data = await response.json();

            if (data.status === 'success') {
                fetchGroups();
            }

            return data;  // Ensure data contains a 'status' field
        } catch (error) {
            return { status: 'error', message: 'Failed to create font group. Please try again.' };
        }

    };

    const handleDeleteGroup = async (groupId) => {
        const confirmDelete = window.confirm('Are you sure you want to delete this font group?');

        if (confirmDelete) {
            const response = await fetch('http://localhost/font_system/backend/index.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ deleteGroup: groupId }),
            });

            const data = await response.json();
            if (data.status === 'success') {
                fetchGroups(); // Refresh the list of font groups after deletion
            }
        }
    };

    const handleDeleteFont = async (fontId) => {
        const confirmDelete = window.confirm('Are you sure you want to delete this font?');

        if (confirmDelete) {
            const response = await fetch('http://localhost/font_system/backend/index.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ deleteFont: fontId }),
            });

            const data = await response.json();
            if (data.status === 'success') {
                fetchFonts(); // Refresh the list of fonts after deletion
            }
        }
    };

    return (
        <div className="container">
            <h1>Font Group System</h1>

            {/* Upload Font Section */}
            <FontUpload handleUpload={handleUpload} />

            {/* Font List Table */}
            <h4>Uploaded Fonts</h4>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>File Name</th>
                        <th>Actions</th> {/* Add actions column */}
                    </tr>
                </thead>
                <tbody>
                    {fonts.map((font) => (
                        <tr key={font.id}>
                            <td>{font.id}</td>
                            <td>{font.file_name}</td>
                            <td>
                                <button
                                    onClick={() => handleDeleteFont(font.id)}
                                    className="btn btn-danger"
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Font Group Form */}
            <FontGroupForm fonts={fonts} handleGroupSubmit={handleGroupSubmit} />

            {/* Font Groups List Table with Font Count */}
            <h4>Font Groups</h4>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Group Name</th>
                        <th>Fonts</th>
                        <th>Font Count</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {groups.map((group) => (
                        <tr key={group.id}>
                            <td>{group.id}</td>
                            <td>{group.group_name}</td>
                            <td>{group.fonts}</td>
                            <td>{group.font_count}</td>
                            <td>
                                <button
                                    onClick={() => handleDeleteGroup(group.id)}
                                    className="btn btn-danger"
                                >
                                    Delete
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default App;
